    private void mouseclicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_mouseclicked
        // TODO add your handling code here:
        if (evt.getSource()==btn_about){
        Panel_about.setVisible(true);
        Panel_location.setVisible(false);
        Panel_gallery.setVisible(false);
        Panel_services.setVisible(false);
        }
        
        if (evt.getSource()==btn_location){
        Panel_about.setVisible(false);
        Panel_location.setVisible(true);
        Panel_gallery.setVisible(false);
        Panel_services.setVisible(false);
        }
        
        if (evt.getSource()==btn_gallery){
        Panel_about.setVisible(false);
        Panel_location.setVisible(false);
        Panel_gallery.setVisible(true);
        Panel_services.setVisible(false);
        }
        
        if (evt.getSource()==btn_services){
        Panel_about.setVisible(false);
        Panel_location.setVisible(false);
        Panel_gallery.setVisible(false);
        Panel_services.setVisible(true);
        }
    }//GEN-LAST:event_mouseclicked

